import React, {useEffect, useState} from 'react';
import ReactDOM from 'react-dom';

const API_HOST = 'https://jsonplaceholder.typicode.com';
const INVENTORY_API_URL = `${API_HOST}/users`;

function App() {
    const [data, setData] = useState([]);

    const fetchInventory = () => {
        fetch(`${INVENTORY_API_URL}`)
            .then(res => res.json())
            .then(json => setData(json));
    }

    useEffect(() => {
        fetchInventory();
    }, []);


    const [inEditMode, setInEditMode] = useState({
        status: false,
        rowKey: null
    });

    const [phone, setPhone] = useState(null);

    /**
     *
     * @param id - The id of the product
     * @param currentPhone - The current unit price of the product
     */
    const onEdit = ({id, currentPhone}) => {
        setInEditMode({
            status: true,
            rowKey: id
        })
        setPhone(currentPhone);
    }

    /**
     *
     * @param id
     * @param newPhone
     */
    const updateData = ({id, newPhone}) => {
        fetch(`${INVENTORY_API_URL}/${id}`, {
            method: "PATCH",
            body: JSON.stringify({
                user_phone: newPhone
            })
        })
            .then(response => response.json())
            .then(json => {
                // reset inEditMode and unit price state values
                onCancel();

                // fetch the updated data
                fetchInventory();
            })
    }

    /**
     *
     * @param id -The id of the product
     * @param newPhone - The new unit price of the product
     */
    const onSave = ({id, newPhone}) => {
        updateData({id, newPhone});
    }

    const onCancel = () => {
        // reset the inEditMode state value
        setInEditMode({
            status: false,
            rowKey: null
        })
        // reset the unit price state value
        setPhone(null);
    }
    const onDeleted = () => {
    console.log('deleted');

}

    return (
        <div className="container">
            <h1>Simple Inventory Table</h1>
            <table>
                <thead>
                <tr>
                    <th>Product Name</th>
                    <th>Product Category</th>
                    <th>Unit Price</th>
                    <th>Action</th>
                </tr>
                </thead>
                <tbody>
                {
                    data.map((user) => (
                        <tr key={user.id}>
                            <td>{user.name}</td>
                            <td>{user.email}</td>
                            <td>
                                {
                                    inEditMode.status && inEditMode.rowKey === user.id ? (
                                        <input value={phone}
                                               onChange={(event) => setPhone(event.target.value)}
                                        />
                                    ) : (
                                        user.user_phone
                                    )
                                }
                            </td>
                            <td>
                                {
                                    inEditMode.status && inEditMode.rowKey === user.id ? (
                                        <React.Fragment>
                                            <button
                                                className={"btn-success"}
                                                onClick={() => onSave({id: user.id, newPhone: phone})}
                                            >
                                                Save
                                            </button>

                                            <button
                                                className={"btn-secondary"}
                                                style={{marginLeft: 8}}
                                                onClick={() => onCancel()}
                                            >
                                                Cancel
                                            </button>
                                        </React.Fragment>
                                    ) : (
                                        <button
                                            className={"btn-primary"}
                                            onClick={() => onEdit({id: user.id, currentPhone: user.user_phone})}
                                        >
                                            Edit
                                        </button>
                                    )
                                }
                            </td>
                            <td>
                            <button
                                   className={"btn-primary"}
                                   onClick={() => onDeleted()}
                                        >
                                    Deleted
                             </button>
                            </td>
                        </tr>
                    ))
                }
                </tbody>
            </table>
        </div>
    );
}

ReactDOM.render(<App/>, document.getElementById('root'));
