import React from "react";
import ReactDOM from "react-dom";

    var datasByRow = [
    	// {band: 'The Rolling Stones', type: 'Rock' , like: 'Yes'},
    	// {band: `Guns N' Roses`, type: 'Hard Rock', like: 'Yes'},
       { band: "Test", type: 'test', like: 'No'},
       { value: 1 },
       { value: "test data" },
       { value: 20000000 },
       { value: "more test data text for example" }
    
    ];

    class Inputs extends React.Component {
		constructor(props) {
    		super(props);
    		this.state = { band:'', type:'', like:'' };
    		this.handleClear = this.handleClear.bind(this);
    	}

    	handleChange(propertyName, e) {
    		var change = {};
    		change[propertyName] = e.target.value;
    		this.setState(change);
    	}

    	handleClear() {
    		this.setState({ band:'', type:'', like:'' });
    	}

    	render(){
    		return 	<div className='row inputs'>
    			<Input name='band' change={this.handleChange.bind(this, 'band')} value={this.state.band} placeholder='Band' />
    			<Input name='type' change={this.handleChange.bind(this, 'type')} value={this.state.type} placeholder='Type' />
    			<Input name='like' change={this.handleChange.bind(this, 'like')} value={this.state.like} placeholder='Like' />
    			<div className='col-md-3'><AddButton newData={this.state} create={this.props.onCreate} clear={this.handleClear} /></div>
    		</div>
    	}
    }

     class Input extends React.Component {
     	constructor(props) {
    		super(props);
    		this.state = { value: this.props.value };
    	}

    	render(){
    		return <div className='col-md-3'>
    			<input type='text' name={this.props.name} placeholder={this.props.placeholder} onChange={this.props.change} value={this.props.value} className='form-control'/>
    		</div>
    	}
	}

    class Column extends React.Component {
    	render(){
    		return <td style={{ "width": "30%" }}>
    			{this.props.placeholder}
    		</td>
    	}
	}

	class DeleteButton extends React.Component {

		onClick() {
			this.props.delete.call(null, this.props.uuid);
		}

    	render(){
    		return <td style={{ "width": "10%" }}>
    			<button type="button" className="btn btn-danger" onClick={this.onClick.bind(this)}>Delete</button>
    		</td>
    	}
	}

	class AddButton extends React.Component {

		onClick(){
			if(this.props.newData.band.length > 0 && this.props.newData.type.length > 0){
				this.props.create.call(null, this.props.newData.band, this.props.newData.type, this.props.newData.like);
				this.props.clear.call(null);
			}
		}

    	render(){
    		return <button type="button" style={{ "width": "100%" }} className="btn btn-success" onClick={this.onClick.bind(this)}>Add</button>
    	}
	}

    class Row extends React.Component {
    	render(){
    		return  <tr>
				<Column type='band' placeholder={this.props.band} />
				<Column type='type' placeholder={this.props.type} />
				<Column type='like' placeholder={this.props.like} />
				<DeleteButton uuid={this.props.uuid} delete={this.props.onDelete} />
	    	</tr>
    	}
    }

    class Table extends React.Component {
    	render(){
    		return <table className='table' >
    			<thead>
					<tr>
      					<th scope="col" style={{ "width": "30%" }}>Band</th>
      					<th scope="col" style={{ "width": "30%" }}>Type</th>
      					<th scope="col" style={{ "width": "30%" }}>Like</th>
      					<th scope="col" style={{ "width": "10%" }}></th>
   					</tr>
  				</thead>
    		
				<tbody>
					{
		    			this.props.datas.map((data, i) => {
		    				return <Row key={i} uuid={i} band={data.band} type={data.type} like={data.like} onDelete={this.props.onDelete} />
		    			})
	    			}
				</tbody>
    		</table>
    	}
    }

    class Container extends React.Component {
    	constructor(props) {
    		super(props);
    		this.state = { datas: datasByRow, counter: datasByRow.lenght };
    		this.onDelete = this.onDelete.bind(this);
    		this.onCreate = this.onCreate.bind(this);
    	}

    	onDelete(id) {
    		var data_list = this.state.datas;
    		data_list.splice(id, 1);
    		this.setState({datas: data_list, counter: data_list.lenght});
    	}

    	onCreate(band, type, like){
    		this.setState({datas: this.state.datas.concat([{band: band, type: type, like: like}])});
    	}

    	render(){
    		return <div>
    			<Inputs onCreate={this.onCreate}/>
    			<Table datas={this.state.datas} onDelete={this.onDelete} />
			</div>
    	}
    }

	ReactDOM.render(
	  <Container />,
	  document.getElementById('container'),
	);
